echo -n "ILO admin password:"; read -s password; echo; for c in {1..7}; do nstart=$[(c-1)*45]; echo $c" "$nstart; for i in {1..15}; do I=$[nstart+i]; if ! (( $c % 7 )) && (( $i > 9 )); then break; fi; wget -q -O - https://queue.taskcluster.net/v1/provisioners/releng-hardware/worker-types/gecko-t-linux-talos/workers/mdc1/t-linux64-ms-$(printf "%03g" ${I}) >/dev/null 2>&1 || (echo -e "$password\n" | ./reboot.exp --hostname "Administrator@moon-chassis-${c}.inband.releng.mdc1.mozilla.com" --addr "c${i}n1"); done; done | tee state.$(date +"%H:%M:%S").log